import React, { useState } from 'react';
import { Target, Zap, Monitor } from 'lucide-react';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import { collection, addDoc } from 'firebase/firestore';
import { auth, db } from '../firebase';

const LandingPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    try {
      // Create user account
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      
      // Save user data to Firestore
      await addDoc(collection(db, 'earlyAccess'), {
        email: email,
        userId: userCredential.user.uid,
        timestamp: new Date(),
        pricing: 'early-bird',
        amount: 29.99
      });

      // Redirect to success page
      window.location.href = '/success';
    } catch (error) {
      setError(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-black text-white font-mono">
      {/* Header */}
      <div className="text-center pt-8 pb-4">
        <h1 className="text-lg font-medium text-gray-300">AI Fortnite Coach - PathGen</h1>
      </div>

      {/* Main Content */}
      <div className="max-w-4xl mx-auto px-6 py-12">
        {/* Hero Section */}
        <div className="text-center mb-16 animate-fade-in">
          <h2 className="text-5xl md:text-6xl font-bold leading-tight mb-6">
            Level up faster with<br />
            <span className="text-accent">AI-powered</span><br />
            coaching
          </h2>
          
          <p className="text-xl text-gray-300 mb-12 max-w-2xl mx-auto leading-relaxed">
            Personalized Fortnite training paths, aim drills, and PC<br />
            optimization.<br />
            <span className="text-accent font-semibold">All powered by AI.</span>
          </p>
        </div>

        {/* Sign-up Form */}
        <div className="max-w-md mx-auto mb-12 animate-slide-up">
          <form onSubmit={handleSubmit} className="bg-gray-900 rounded-lg p-8 border border-gray-800">
            <div className="space-y-6">
              <div>
                <label htmlFor="email" className="block text-sm font-medium text-gray-300 mb-2">
                  Enter your email
                </label>
                <input
                  type="email"
                  id="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="w-full px-4 py-3 bg-gray-800 border border-gray-700 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:border-accent focus:ring-1 focus:ring-accent transition-colors"
                  placeholder="your@email.com"
                  required
                />
              </div>
              
              <div>
                <label htmlFor="password" className="block text-sm font-medium text-gray-300 mb-2">
                  Create a password (min 8 characters)
                </label>
                <input
                  type="password"
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="w-full px-4 py-3 bg-gray-800 border border-gray-700 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:border-accent focus:ring-1 focus:ring-accent transition-colors"
                  placeholder="••••••••"
                  minLength={8}
                  required
                />
              </div>
              
              <button
                type="submit"
                disabled={isLoading}
                className="w-full bg-white text-black font-semibold py-3 px-6 rounded-lg hover:bg-gray-100 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {isLoading ? 'Creating Account...' : 'Get Early Access'}
              </button>
            </div>
            
            {error && (
              <div className="mt-4 p-3 bg-red-900/20 border border-red-500/30 rounded-lg text-red-300 text-sm">
                {error}
              </div>
            )}
          </form>
          
          <div className="text-center mt-6">
            <p className="text-gray-400">
              Lock in <span className="bg-gray-800 px-2 py-1 rounded text-white font-semibold">$29.99</span> early-bird pricing
            </p>
          </div>
        </div>

        {/* Features Section */}
        <div className="grid md:grid-cols-3 gap-8 max-w-5xl mx-auto animate-slide-up">
          <div className="text-center">
            <div className="inline-flex items-center justify-center w-16 h-16 bg-accent/10 rounded-full mb-6">
              <Target className="w-8 h-8 text-accent" />
            </div>
            <h3 className="text-xl font-bold mb-4">Smart Training Paths</h3>
            <p className="text-gray-400 leading-relaxed">
              AI analyzes your gameplay and builds<br />
              custom training sequences
            </p>
          </div>
          
          <div className="text-center">
            <div className="inline-flex items-center justify-center w-16 h-16 bg-accent/10 rounded-full mb-6">
              <Zap className="w-8 h-8 text-accent" />
            </div>
            <h3 className="text-xl font-bold mb-4">Adaptive Drills</h3>
            <p className="text-gray-400 leading-relaxed">
              Dynamic exercises that evolve with<br />
              your performance
            </p>
          </div>
          
          <div className="text-center">
            <div className="inline-flex items-center justify-center w-16 h-16 bg-accent/10 rounded-full mb-6">
              <Monitor className="w-8 h-8 text-accent" />
            </div>
            <h3 className="text-xl font-bold mb-4">PC Optimization</h3>
            <p className="text-gray-400 leading-relaxed">
              Maximize FPS and eliminate<br />
              stuttering for peak performance
            </p>
          </div>
        </div>
      </div>

      {/* Footer */}
      <div className="text-center py-8">
        <div className="flex items-center justify-center space-x-2 text-gray-500">
          <div className="w-6 h-6 bg-white rounded-full flex items-center justify-center">
            <span className="text-black text-xs font-bold">b</span>
          </div>
          <span className="text-sm">Made in Bolt</span>
        </div>
      </div>
    </div>
  );
};

export default LandingPage;
