import React from 'react';
import { Check } from 'lucide-react';

const ConfirmationPage = () => {
  return (
    <div className="min-h-screen bg-black text-white font-mono flex items-center justify-center">
      <div className="text-center max-w-md mx-auto px-6 animate-fade-in">
        {/* Success Icon */}
        <div className="inline-flex items-center justify-center w-24 h-24 bg-green-500 rounded-full mb-8">
          <Check className="w-12 h-12 text-white" />
        </div>
        
        {/* Main Message */}
        <h1 className="text-4xl font-bold mb-6">You're in.</h1>
        
        {/* Pricing Info */}
        <p className="text-lg text-gray-300 mb-8">
          Your early-bird price of <span className="bg-gray-800 px-2 py-1 rounded font-bold">$29.99</span> is locked in.
        </p>
        
        {/* Email Confirmation */}
        <p className="text-sm text-gray-400 mb-12">
          We'll email you at <span className="bg-gray-800 px-2 py-1 rounded font-semibold">jlbender2005@gmail.com</span> when we launch.
        </p>
        
        {/* Footer */}
        <div className="flex items-center justify-center space-x-2 text-gray-500">
          <div className="w-6 h-6 bg-white rounded-full flex items-center justify-center">
            <span className="text-black text-xs font-bold">b</span>
          </div>
          <span className="text-sm">Made in Bolt</span>
        </div>
      </div>
    </div>
  );
};

export default ConfirmationPage;
